mapper.select:
SELECT A.COMPANY_CD , A.PLANT_CD , B.PLANT_NM , A.EQP_CD , A.EQP_NM , A.ENV_TSK_FG_CD , A.ENV_TSK_FG_NM , A.DSCHFCLT_CD , A.DSCHFCLT_NM , A.SQ_NO , A.RMK_DC FROM EH_WDSCHFCLT_MST A LEFT OUTER JOIN MA_PLANT_MST B ON A.COMPANY_CD = B.COMPANY_CD AND A.PLANT_CD = B.PLANT_CD WHERE A.COMPANY_CD = #{P_COMPANY_CD}
AND A.PLANT_CD = #{P_PLANT_CD}
AND ( UPPER(A.DSCHFCLT_NM) LIKE '%'||#{P_DSCHFCLT_NM}||'%' )

SELECT COUNT(1) 
FROM @{dzparam_dbname}EH_WDSCHFCLT_MST 
WHERE COMPANY_CD = #{company_cd}
	AND PLANT_CD = #{plant_cd} 
	AND ENV_TSK_FG_CD = #{env_tsk_fg_cd} 
	AND DSCHFCLT_CD = #{dschfclt_cd}

mapper.delete:
DELETE
FROM	@{dzparam_dbname}EH_WDSCHFCLT_MST
WHERE	COMPANY_CD = #{company_cd}
	AND		PLANT_CD	= #{plant_cd} 
	AND		ENV_TSK_FG_CD	= #{env_tsk_fg_cd}
	AND		DSCHFCLT_CD		= #{dschfclt_cd}
